import api from './index';

const config = () => {
  const tokenSessao = sessionStorage.getItem('token');
  const tokenLocal = localStorage.getItem('token');
  const token = tokenSessao?tokenSessao:tokenLocal;
  return {
    headers: { 'Authorization': `${token}` }
  }
}
export default {
  async getUsers() {    
    const resultado = await api().get('/users/0', config());
    return resultado.data;
  },
  async getPerfil(perfil_id) {
    const resultado = await api().get(`/perfil/${perfil_id}`, config());
    return resultado.data;
  },
  async getPerfis() {    
    console.log(`no getPerfis`)
    const resultado = await api().get('/perfis', config());
    return resultado.data;
  },
  async getPermissao(pessoa_id) {
    const resultado = await api().get(`/permissao/${pessoa_id}`, config());
    return resultado.data;
  },
  createUser(data) {
    return api().post('/users', data, config());
  },
  updateUser(data) {
    return api().put(`/users/${data.user_id}`, data, config());
  },
  deleteUser(user_id) {
    return api().delete(`/users/${user_id}`, null, config());
  },
};
