import api from './index';

const config = () => {
  const tokenSessao = sessionStorage.getItem('token');
  const tokenLocal = localStorage.getItem('token');
  const token = tokenSessao?tokenSessao:tokenLocal;
  return {
    headers: { 'Authorization': `${token}` }
  }
}
export default {
  async getUsers() {    
    const resultado = await api().get('/users/0', config());
    return resultado.data;
  },
  async getUser(id) {
    const resultado = await api().get(`/users/${id}`, null, config());
    return resultado.data;
  },
  createUser(data) {
    return api().post('/users', data, config());
  },
  updateUser(data) {
    return api().put(`/users/${data.id}`, data, config());
  },
  deleteUser(id) {
    return api().delete(`/users/${id}`, null, config());
  },
};
